const searchInput = document.querySelector("#search-input");

searchInput.addEventListener("keydown", function(event) {
  if(event.code === "Enter") {
    search();
  }
});

function search() {
  const input = searchInput.value;

  window.location.href = "https://www.google.com/search?q=" + input +"&hl=en&ei=pxuzY7UExPXj4Q_0h5ygBg&ved=0ahUKEwj188PFu6n8AhXE-jgGHfQDB2QQ4dUDCA8&uact=5&oq=" + input +"&gs_&sclient=gws-wiz-serp"
}